     Eigen::Vector3d h_px4f(p_(0), p_(1), p_(2));

     //measurement Jacobian
     Eigen::Matrix<double, 3, NUM_ERROR_STATES> H;
     H.setZero();
     H(0, dPN) = 1;
     H(0, dPE) = 1;
     H(0, dPD) = -1;

     //Compute residual covariance
     Eigen::Vector3d S = H * P_ * H.transpose() + R_px4flow;

     //Compute Kalman gain
     Eigen::Matrix<double, NUM_ERROR_STATES> K = P * H.transpose() * S.inverse();

     //Compute measurement error
     Eigen::Vector2d residual = px4f_val - h_px4f;

     //Compute delta_x
     Eigen::Matrix<double, NUM_ERROR_STATES, 1> delta_x = K * residual;

     //Update state & covariance
